window.OpenTypeChange = function(v8){
  if(v8.Form.OpenType.Key == 'Diy'){
    v8.FieldSet('DiyTableId', 'Visible', true);
    v8.FieldSet('ComponentPath', 'Visible', true);
    v8.FieldSet('ComponentName', 'Visible', true);
    v8.FieldSet('ReportName', 'Visible', false);
  }else if(v8.Form.OpenType.Key == 'Component'){//定制
    v8.FieldSet('DiyTableId', 'Visible', false);
    v8.FieldSet('ComponentPath', 'Visible', true);
    v8.FieldSet('ComponentName', 'Visible', false);
    v8.FieldSet('ReportName', 'Visible', false);
  } else if (v8.Form.OpenType.Key == 'Report'){
    v8.FieldSet('DiyTableId', 'Visible', false);
    v8.FieldSet('ComponentPath', 'Visible', true);
    v8.FieldSet('ComponentName', 'Visible', true);
    v8.FieldSet('ReportName', 'Visible', true);
  } else if (v8.Form.OpenType.Key == 'Iframe' || v8.Form.OpenType.Key == 'SecondMenu'){
    v8.FieldSet('DiyTableId', 'Visible', false);
    v8.FieldSet('ComponentPath', 'Visible', false);
    v8.FieldSet('ComponentName', 'Visible', false);
    v8.FieldSet('ReportName', 'Visible', false);
  }else{
    v8.FieldSet('DiyTableId', 'Visible', false);
    v8.FieldSet('ComponentPath', 'Visible', false);
    v8.FieldSet('ComponentName', 'Visible', false);
    v8.FieldSet('ReportName', 'Visible', false);
  }
  if (v8.Form.OpenType.Key == 'SecondMenu'){
    v8.FieldSet('SecondMenuWidth', 'Visible', true);
    v8.FieldSet('SecondMenuHeight', 'Visible', true);
    v8.FieldSet('SecondMenuLineCount', 'Visible', true);
    v8.FieldSet('SecondMenuColumn', 'Visible', true);
    v8.FieldSet('SecondMenuRow', 'Visible', true);
  }else{
    v8.FieldSet('SecondMenuWidth', 'Visible', false);
    v8.FieldSet('SecondMenuHeight', 'Visible', false);
    v8.FieldSet('SecondMenuLineCount', 'Visible', false);
    v8.FieldSet('SecondMenuColumn', 'Visible', false);
    v8.FieldSet('SecondMenuRow', 'Visible', false);
  }
}
